import { Injectable } from '@angular/core';
import { AlunosModel } from './alunos';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';
import { environment } from 'src/environments/environment.development';

@Injectable({
  providedIn: 'root'
})
export class AlunosService {
  private url = `${environment.apiUrl}/${this.url}`;
  alunosLista: AlunosModel[] = [];

  constructor(private http: HttpClient) { }

  getTodosAlunos(): Observable<AlunosModel[]> {
    return this.http.get<AlunosModel[]>();
  }

  getAlunoId(id: number): Observable<AlunosModel>{
    return this.http.get<AlunosModel>(`${environment.apiUrl}/${this.url}/${id}`);
  }

  novoAluno(novoAlunoRequest: AlunosModel): Observable<AlunosModel> {
    return this.http.post<AlunosModel>(
      `${environment.apiUrl}/${this.url}`,
      novoAlunoRequest
    );
  }


}
