import { Component, Input } from '@angular/core';
import { AlunosModel } from '../alunos';

@Component({
  selector: 'app-alunos',
  templateUrl: './alunos.component.html',
  styleUrls: ['./alunos.component.css']
})
export class AlunosComponent {
  @Input() aluno: AlunosModel | undefined;
}
<section class="listing">
    <h2 class="listing-heading">{{ aluno?.nome }}</h2>
    <p class="listing-data">{{ aluno?.idade }}, {{ aluno?.curso }}</p>
    <a *ngIf="aluno.id" [routerLink]="['editar', aluno.id]" routerLinkActive="router-link-active">Editar</a>
</section>
