import { Component, OnInit } from '@angular/core';
import { AlunosModel } from '../alunos';
import { ActivatedRoute, Router } from '@angular/router';
import { AlunosService } from '../alunos.service';

@Component({
  selector: 'app-editar-aluno',
  templateUrl: './editar-aluno.component.html',
  styleUrls: ['./editar-aluno.component.css']
})
export class EditarAlunoComponent implements OnInit {

  alunoInfo: AlunosModel = { id: 0, nome: "", idade: 0, curso: "" };

  constructor(private route: ActivatedRoute, private alunosService: AlunosService, private router: Router) { }


  ngOnInit(): void {
    this.route.paramMap.
    subscribe(params => {
      const idParam = params.get("id");

      const id = idParam !== null ? +idParam : null;

      if (id !== null && !isNaN(id)) { this.alunosService.getAlunoId(id)
          .subscribe(response => {this.alunoInfo = response; });
      } 


      else (id === null) {
        console.log("ID não encontrado");
      }
    });
  }

  atualizarAluno() {
    this.a.updateStudent(this.studentDetails.id, this.studentDetails)
    .subscribe({
      next: (response) => {
        this.router.navigate(["alunos"]);
      }
    });
  }

}
