}
<section class="listing" *ngFor="let aluno of alunosLista">
    <h2 class="listing-heading">{{ aluno.nome }}</h2>
    <p class="listing-data">{{ aluno.idade }}, {{ aluno.curso }}</p>
    <a [routerLink]="['editar', aluno.id]" routerLinkActive="router-link-active">Editar</a>
</section>
